import React from "react";

import "./App.css";

// import * as api from "./api";

import { fetchProducts } from "./api";

function App() {
  const [filter, setFilter] = React.useState({
    brands: [],
    priceFrom: 0,
    priceTo: 0,
    keys: [],
  });
  const [products, setProducts] = React.useState([]);

  React.useEffect(() => {
    fetchProducts({}).then((data) => setProducts(data));
  }, []);

  // React.useEffect(() => {
  //   api.fetchProducts(filter).then((res) => setProducts(res));
  // }, [filter]);

  // const handleChangeBrand = (target) => {
  //   if (filter.brands.indexOf(target.value) > -1) {
  //     setFilter({
  //       ...filter,
  //       brands: filter.brands.filter((el) => el != target.value),
  //     });
  //   } else {
  //     setFilter({
  //       ...filter,
  //       brands: filter.brands.concat(target.value),
  //     });
  //   }
  // };

  return (
    <div className="container">
      <div className="form">
        <form>
          <label htmlFor="fender">Fender</label>
          <input type="checkbox" id="fender" />
          <br />
          <label htmlFor="gibson">Gibson</label>
          <input type="checkbox" id="gibson" /> <br />
          <label htmlFor="epiphone">Epihone</label>
          <input type="checkbox" id="epiphone" />
          <br />
          <label htmlFor="price">From</label>
          <input
            type="number"
            id="price"
            onChange={(e) => {
              setFilter({
                ...filter,
                priceFrom: e.target.value,
              });
            }}
          />
          <label htmlFor="price">To</label>
          <input
            type="number"
            id="price"
            onChange={(e) => {
              setFilter({
                ...filter,
                priceFrom: e.target.value,
              });
            }}
          />
        </form>
      </div>
      <div className="catalog">
        {products.map((product) => (
          <div>
            {console.log(product)}
            <div>
              <img src={product.image} />
            </div>
            <div>
              <h3>{product.name}</h3>
              <p>{product.price}</p>
              <p>{product.type}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
