import React from 'react';

import './App.css';

import * as api from './api';

function App() {
  const [filter, setFilter] = React.useState({
    brands: [],
    priceFrom:0,
    priceTo:0,
    keys: []
  });
  const [products, setProducts] = React.useState([]);

  React.useEffect(() => {
    api.fetchProducts(filter).then((res) => setProducts(res));
  }, [filter]);

  const handleChangeBrand = (target) => {
    if(filter.brands.indexOf(target.value) > -1) {
      setFilter({
        ...filter,
        brands: filter.brands.filter(el => el != target.value)
      })
    } else {
      setFilter({
        ...filter,
        brands: filter.brands.concat(target.value)
      })
    }
  }

  return (
    <div className="App">
      <div className="Filters">
        <div>
          <ul className="list-group list-group-flush">
            <li className="list-group-item">
              <div className="custom-control custom-checkbox">
                <input
                  value="Korg"
                  type="checkbox"
                  className="custom-control-input"
                  id="check1"
                  onChange={(e)=>{
                    handleChangeBrand(e.target);
                  }}
                />
                <label className="custom-control-label" htmlFor="check1">
                  Korg
                </label>
              </div>
            </li>
            <li className="list-group-item">
              <div className="custom-control custom-checkbox">
                <input
                  value="Yamaha"
                  type="checkbox"
                  className="custom-control-input"
                  id="check2"
                  onChange={(e)=>{
                    handleChangeBrand(e.target);
                  }}
                />
                <label className="custom-control-label" htmlFor="check2">
                  Yamaha
                </label>
              </div>
            </li>
            <li className="list-group-item">
              <div className="custom-control custom-checkbox">
                <input
                  value="Roland"
                  type="checkbox"
                  className="custom-control-input"
                  id="check3"
                  onChange={(e)=>{
                    handleChangeBrand(e.target);
                  }}
                />
                <label className="custom-control-label" htmlFor="check3">
                  Roland
                </label>
              </div>
            </li>
          </ul>
        </div>

        <div>
          От: <input type="number" onChange={(e)=>{
            setFilter({
              ...filter,
              priceFrom: e.target.value
            })
          }} />
          <br />
          До: <input type="number" onChange={(e)=>{
            setFilter({
              ...filter,
              priceTo: e.target.value
            })
          }}  />
        </div>

        <div>
          <ul className="list-group list-group-flush">
            <li className="list-group-item">
              <div className="custom-control custom-checkbox">
                <input
                  type="checkbox"
                  className="custom-control-input"
                  id="check1"
                />
                <label className="custom-control-label" htmlFor="check1">
                  61
                </label>
              </div>
            </li>
            <li className="list-group-item">
              <div className="custom-control custom-checkbox">
                <input
                  type="checkbox"
                  className="custom-control-input"
                  id="check2"
                />
                <label className="custom-control-label" htmlFor="check2">
                  76
                </label>
              </div>
            </li>
            <li className="list-group-item">
              <div className="custom-control custom-checkbox">
                <input
                  type="checkbox"
                  className="custom-control-input"
                  id="check3"
                />
                <label className="custom-control-label" htmlFor="check3">
                  88
                </label>
              </div>
            </li>
          </ul>
        </div>
      </div>
      <div className="Products">
        {products.map((product) => (
          <div className="ProductItem">
            <div className="ProductImage">
              <img src={product.image} />
            </div>
            <div>
              <h3>{product.name}</h3>
              <p>{product.price}</p>
              <p>{product.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
